    const min = 700;
    const max = 4000;
    const zone1register = 11128;

    var value = msg.payload * 100;
    var ru = parseInt(res[2]);

    // Make sure we are within bounds supported by the system
    if (value < 700) {
        value = Math.max(value, min);
    }
    if (value > 4000) {
        value = Math.min(value, max);
    }

    msg.payload = {
        'value': [value],
        'fc': 16,
        'address': zone1register + (ru - 1),
        'quantity': 1
    }

}

return msg;

